import React from 'react';
import { socket } from './services/socketService';
import ChatWindow from './components/ChatWindow/ChatWindow';

class App extends React.Component {
    componentDidMount() {
        socket.on('users', function(userList,nickList) {
          console.log(userList, nickList);
            this._populateUserList(userList);
            this._populateNicksList(nickList);
        });
    }
    _populateUserList(userList) {
        this.setState({
            users: userList.map((u, idx) => userList[`${idx}`])
        });
      }
    constructor(props) {
        super(props);
        this.state = {
            users: [],
            nicks:[]
        };
    }
    render() {
        const { users, nicks } = this.state;
        return (
            <div className="container">
                <ChatWindow users={ nicks } />
            </div>
        );
    }
};

export default App;
