import React from 'react';
import { socket } from './services/socketService';
//import ChatWindow from './components/ChatWindow/ChatWindow';
import ChatLobby from './components/ChatLobby/ChatLobby';

class App extends React.Component {
    componentDidMount() {
        socket.on('users', userList => {
            this._populateUserList(userList);
        });
        socket.on('chatRooms', rooms =>{
            this.setState({
                chatRooms: rooms
            })
        })
    }
    _populateUserList(userList) {
        this.setState({
            users: userList.map((u, idx) => `User ${idx + 1}`)
        });
    }
    constructor(props) {
        super(props);
        this.state = {
            users: [],
            chatRooms: new Object()
        };
    }
    render() {
        const { users, rooms } = this.state;
        console.log(rooms)
        return (
            <div className="container">
                <ChatLobby users={ users } />
            </div>
        );
    }
};

export default App;
