        socket.on('users', userList => {
          console.log(userList);
            this._populateUserList(userList);
        });
    }
    _populateUserList(userList) {
        this.setState({
            users: userList.users.map((u, idx) => userList.users[`${idx}`]),
            users: userList.users.map((u, idx) => userList.nicks[`${idx}`])
