import React from 'react';
import { socket } from './services/socketService';
import ChatWindow from './components/ChatWindow/ChatWindow';

class App extends React.Component {
    componentDidMount() {
        socket.on('users', function (data){
            console.log(data);
            this._populateUserList(data);
        });
    }
    _populateUserList(dataList) {
        this.setState({
            users: dataList.users.map((u, idx) => dataList.users[`${idx}`]),
            nicks: dataList.users.map((u, idx) => dataList.nicks[`${idx}`])
        });
    }
    constructor(props) {
        super(props);
        this.state = {
            users: []
        };
    }
    render() {
        const { users } = this.state;
        return (
            <div className="container">
                <ChatWindow users={ users } />
            </div>
        );
    }
};

export default App;
