import React from 'react';
import { socket } from './services/socketService';
import ChatWindow from './components/ChatWindow/ChatWindow';

class App extends React.Component {
    componentDidMount() {
        socket.on('users', function(data) {
            this._populateUserList(data.users);
            this._populateNicksList(data.nicks);
        });
    }
    _populateUserList(userList) {
        this.setState({
            users: userList.map((u, idx) => userList[`${idx}`])
        });
      }
    _populateNicksList(nicksList) {
        this.setState({
            nicks: nicksList.map((u, idx) => nicksList[`${idx}`])
        });
    }
    constructor(props) {
        super(props);
        this.state = {
            users: [],
            nicks:[]
        };
    }
    render() {
        const { users, nicks } = this.state;
        return (
            <div className="container">
                <ChatWindow users={ nicks } />
            </div>
        );
    }
};

export default App;
